import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { catchError, map, Observable, of } from 'rxjs';
import { environment } from '../../environments/environment';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = environment.apiUrl;

  constructor(private http: HttpClient) {}


  register(userData: any): Observable<any> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    return this.http.post(`${this.apiUrl}/auth/register/`, userData ,{ headers });
  }

  login(credentials: { email: string; password: string }): Observable<any> {
    return this.http.post(`${environment.apiUrl}/auth/login/`, credentials);
  }

  verifyOTP(data: { email: string; otp: string }): Observable<any> {
    return this.http.post(`${environment.apiUrl}/auth/verify-otp/`, data);
  }

  isTokenValid(token: string): Observable<boolean> {
    return this.http.post<{ valid: boolean }>(`${environment.apiUrl}/auth/validate-token/`, { token }).pipe(
      map((response) => response.valid),
      catchError(() => of(false))
    );
  }

  logout(): void {
    localStorage.removeItem('access_token');
  }



}
