import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';

export interface Patient {
  id: number;
  numero_identite: string;
  nom: string;
  prenom: string;
  age: number;
  numero_telephone: string;
}

@Injectable({
  providedIn: 'root'
})
export class PatientService {
  private apiUrl = environment.apiUrl;

  constructor(private http: HttpClient) {}

  getPatients(): Observable<Patient[]> {

    return this.http.get<Patient[]>(`${this.apiUrl}/auth/patients/`);
  }

  getPatientById(id: number): Observable<Patient> {
    return this.http.get<Patient>(`${this.apiUrl}/auth/patients/${id}/`);
  }

  private getHeaders(): HttpHeaders {
    const token = localStorage.getItem('authToken'); // Assurez-vous que le jeton est stocké dans `localStorage`
    return new HttpHeaders({
      'Content-Type': 'application/json',
      Authorization: `Bearer ${token}` // Ou `Token ${token}` si vous utilisez TokenAuthentication
    });
  }

  addPatient(patient: Patient): Observable<Patient> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    return this.http.post<Patient>(`${this.apiUrl}/auth/patients/`, patient,{ headers });
  }

  updatePatient(patient: Patient): Observable<Patient> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    return this.http.put<Patient>(`${this.apiUrl}/auth/patients/${patient.id}/`, patient,{ headers });
  }

  deletePatient(id: number): Observable<void> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
    return this.http.delete<void>(`${this.apiUrl}/auth/patients/${id}/`,{ headers });
  }
}
