import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { PatientService, Patient } from './../services/patient.service';

@Component({
  selector: 'app-add-patient',
  templateUrl: './add-patient.component.html',
  styleUrls: ['./add-patient.component.css']
})
export class AddPatientComponent {
  patient: Patient = {
    id: 0,
    numero_identite: '',
    nom: '',
    prenom: '',
    age: 0,
    numero_telephone: ''
  };

  constructor(private patientService: PatientService, private router: Router) {}

  addPatient(): void {
    console.log('Data sent to API:', patient);
    this.patientService.addPatient(this.patient).subscribe({
      next: () => {
        alert('Patient ajouté avec succès');
        this.router.navigate(['/home']);
      },
      error: (err) => console.error(err)
    });
  }

  console.log('Data sent to API:', patient);
this.patientService.addPatient(patient).subscribe({
  next: (response) => console.log('Patient added successfully', response),
  error: (err) => console.error('Error adding patient:', err)
});

}
<div class="container">
  <h1>Ajouter un Patient</h1>
  <form (submit)="addPatient()">
    <div>
      <label>Numéro d'identité</label>
      <input type="number" class="form-control" [(ngModel)]="patient.numero_identite" required />
    </div>
    <div>
      <label>Nom</label>
      <input type="text" class="form-control" [(ngModel)]="patient.nom" required />
    </div>
    <div>
      <label>Prénom</label>
      <input type="text" class="form-control" [(ngModel)]="patient.prenom" required />
    </div>
    <div>
      <label>Âge</label>
      <input type="number" class="form-control" [(ngModel)]="patient.age" type="number" required />
    </div>
    <div>
      <label>Numéro de téléphone</label>
      <input type="number" class="form-control" [(ngModel)]="patient.numero_telephone" required />
    </div>
    <button type="submit" class="btn btn-primary">Ajouter</button>
  </form>
</div>
