<div class="container">
  <h1>Modifier le Patient</h1>
  <form (submit)="updatePatient()">
    <div>
      <label>Numéro d'identité</label>
      <input [(ngModel)]="patient.numero_identite" required />
    </div>
    <div>
      <label>Nom</label>
      <input [(ngModel)]="patient.nom" required />
    </div>
    <div>
      <label>Prénom</label>
      <input [(ngModel)]="patient.prenom" required />
    </div>
    <div>
      <label>Âge</label>
      <input [(ngModel)]="patient.age" type="number" required />
    </div>
    <div>
      <label>Numéro de téléphone</label>
      <input [(ngModel)]="patient.numero_telephone" required />
    </div>
    <button type="submit" class="btn btn-primary">Mettre à jour</button>
  </form>
</div>
