import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = `${environment.apiUrl}/auth`;

  constructor(private http: HttpClient, private router: Router) {}

  register(userData: any): Observable<any> {
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    return this.http.post(`${this.apiUrl}/auth/register/`, userData ,{ headers });
  }
  /**
   * Login API call
   * @param credentials {email: string, password: string}
   */
  login(credentials: { email: string; password: string }): Observable<{ message: string }> {
    return this.http.post<{ message: string }>(`${this.apiUrl}/login/`, credentials);
  }

  /**
   * OTP Verification API call
   * @param data {email: string, otp: string}
   */
  verifyOTP(data: { email: string; otp: string }): Observable<{ access_token: string; refresh_token: string }> {
    return this.http.post<{ access_token: string; refresh_token: string }>(`${this.apiUrl}/verify-otp/`, data);
  }

  /**
   * Refresh Token API call
   * @param data {refresh: string}
   */
  refreshToken(data: { refresh: string }): Observable<{ access: string; refresh: string }> {
    return this.http.post<{ access: string; refresh: string }>(`${this.apiUrl}/refresh/`, data);
  }

  /**
   * Logout the user
   */
  logout(): void {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    this.router.navigate(['/login']);
  }

  /**
   * Store tokens in localStorage
   * @param tokens {access: string, refresh: string}
   */
  setTokens(tokens: { access: string; refresh: string }): void {
    localStorage.setItem('access_token', tokens.access);
    localStorage.setItem('refresh_token', tokens.refresh);
  }

  setTokens(response: { access_token: string; refresh_token: string }): void {
    const tokens = {
      access: response.access_token,
      refresh: response.refresh_token,
    };

    localStorage.setItem('access_token', tokens.access);
    localStorage.setItem('refresh_token', tokens.refresh);
  }


  /**
   * Get the access token from localStorage
   */
  getAccessToken(): string | null {
    return localStorage.getItem('access_token');
  }

  /**
   * Get the refresh token from localStorage
   */
  getRefreshToken(): string | null {
    return localStorage.getItem('refresh_token');
  }

  /**
   * Check if the user is authenticated
   */
  isAuthenticated(): boolean {
    return !!this.getAccessToken();
  }
}
